import React, { useContext } from 'react';
import { AppBar, Toolbar, styled, Button, Typography, Box } from '@mui/material'; 
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { DataContext } from '../../context/DataProvider'; // Update the path as per your context location

const Component = styled(AppBar)`
    background: #FFFFFF;
    color: black;
`;

const Container = styled(Toolbar)`
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const WelcomeText = styled(Typography)`
    margin-right: 20px;
`;

const Header = () => {
    const navigate = useNavigate();
    const { account } = useContext(DataContext); // Assuming `account` contains user information

    const logout = async () => navigate('/account');

    return (
        <Component>
            <Container>
                <Box>
                    <Link to='/'>HOME</Link>
                    <Link to='/about'>ABOUT</Link>
                    <Link to='/contact'>CONTACT</Link>
                </Box>
                {account ? (
                    <Box display="flex" alignItems="center">
                        <WelcomeText variant="body1">Welcome, {account.name}</WelcomeText>
                        <Button onClick={logout}>LOGOUT</Button>
                    </Box>
                ) : (
                    <Link to='/account'>LOGIN</Link>
                )}
            </Container>
        </Component>
    );
};

export default Header;
