import React from 'react';
import { AppBar, Toolbar, styled, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { DataContext } from '../../context/DataProvider';
import { useContext } from 'react';
const Component = styled(AppBar)`
  background: #FFFFFF;
  color: black;
  display: flex; /* Make the AppBar a flexbox container */
  align-items: center; /* Align content vertically within the AppBar */
`;

const StyledIcon = styled('img')`
  width: 40px; /* Adjust width as needed */
  height: 40px; /* Adjust height as needed */
  margin-right: 20px; /* Add margin for spacing */
`;

const Container = styled(Toolbar)`
  display: flex; /* Make the Toolbar a flexbox container for control */
  justify-content: space-between; /* Distribute items evenly */
  & > a {
    padding: 20px;
    color: #000;
    text-decoration: none;
  }
`;

const Header = () => {
  const navigate = useNavigate();
  const { account } = useContext(DataContext);

  const logout = async () => navigate('/account');

  return (
    <Component>
      <StyledIcon src={require('favicon.ico')} alt="Your logo or icon" onError={(e) => { e.target.src = 'path/to/default-icon.png'; }} /> {/* Error handling for missing image */}
      <Container>
        <Link to='/'>HOME</Link>
        <Link to='/about'>ABOUT</Link>
        <Link to='/contact'>CONTACT</Link>
        <Link to='/account'>LOGOUT</Link>
        {account ? (
          <Typography variant="body1">Welcome, {account.name}</Typography>
        ) : (
          <Link to='/account'>LOGIN</Link>
        )}
      </Container>
    </Component>
  );
};

export default Header;
