import React, { useContext } from 'react';
import { AppBar, Toolbar, styled, Button, Typography } from '@mui/material'; 
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { DataContext } from '../../context/DataProvider'; // Update the path as per your context location

const Component = styled(AppBar)`
    background: #FFFFFF;
    color: black;
`;

const Container = styled(Toolbar)`
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

const Header = () => {
    const navigate = useNavigate();
    const { account } = useContext(DataContext); // Assuming `account` contains user information

    const logout = async () => navigate('/account');

    return (
        <Component>
            <Container>
                <div>
                    <Link to='/'>HOME</Link>
                    <Link to='/about'>ABOUT</Link>
                    <Link to='/contact'>CONTACT</Link>
                </div>
                <div>
                    {account ? (
                        <Typography variant="body1" style={{ marginRight: '20px' }}>
                            Welcome, {account.name}
                        </Typography>
                    ) : (
                        <Link to='/account'>LOGIN</Link>
                    )}
                </div>
            </Container>
        </Component>
    );
};

export default Header;
