import React, { useContext } from 'react';
import { AppBar, Toolbar, styled, Button, Typography } from '@mui/material'; 
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { DataContext } from '../../context/DataProvider'; // Update the path as per your context location

const Component = styled(AppBar)`
    background: #FFFFFF;
    color: black;
`;

const Container = styled(Toolbar)`
    justify-content: center;
    & > a {
        padding: 20px;
        color: #000;
        text-decoration: none;
    }
`;

const Header = () => {
    const navigate = useNavigate();
    const { account } = useContext(DataContext); // Assuming `account` contains user information

    const logout = async () => navigate('/account');

    return (
        <Component>
            <Container>
                <Link to='/'>HOME</Link>
                <Link to='/about'>ABOUT</Link>
                <Link to='/contact'>CONTACT</Link>
                {account ? (
                    <>
                        <Typography variant="body1">Welcome, {account.name}</Typography>
                        <Button onClick={logout}>LOGOUT</Button>
                    </>
                ) : (
                    <Link to='/account'>LOGIN</Link>
                )}
            </Container>
        </Component>
    );
};

export default Header;
